import React, { useState, useEffect } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import CookingPotBasket from "../CookingPotBasket";
import "./styles.css";

const Navigation = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();

  // Reset scroll and mobile menu when location changes
  useEffect(() => {
    window.scrollTo(0, 0);
    setIsMobileMenuOpen(false);
    // Force a reload if coming from a different path
    const prevPath = sessionStorage.getItem("prevPath");
    if (prevPath !== location.pathname) {
      sessionStorage.setItem("prevPath", location.pathname);
      window.location.reload();
    }
  }, [location]);

  const handleCartClick = () => {
    navigate("/checkout");
  };

  // Enhanced navigation link component
  const NavigationLink = ({ to, children, onClick }) => {
    const handleClick = (e) => {
      e.preventDefault(); // Prevent default Link behavior
      if (onClick) onClick(e);

      // Store current scroll position
      sessionStorage.setItem("scrollPosition", window.scrollY);

      // Navigate and force reload
      window.location.href = to;
    };

    return (
      <Link to={to} onClick={handleClick}>
        {children}
      </Link>
    );
  };

  return (
    <nav className="navigation">
      {/* Desktop Menu */}
      <ul className="desktop-menu">
        <li>
          <NavigationLink to="/">Home</NavigationLink>
        </li>
        <li>
          <NavigationLink to="/menu">Menu</NavigationLink>
        </li>
        <li>
          <NavigationLink to="/Reservation">Book A Table</NavigationLink>
        </li>
        <li>
          <NavigationLink to="/about">About Us</NavigationLink>
        </li>
        <li>
          <NavigationLink to="/contact">Contact</NavigationLink>
        </li>
        <li className="cart-button-container">
          <CookingPotBasket onClick={handleCartClick} />
        </li>
      </ul>

      {/* Mobile Menu Button */}
      <button
        className="mobile-menu-btn"
        onClick={() => setIsMobileMenuOpen(true)}
        aria-label="Open menu"
      >
        <svg
          viewBox="0 0 24 24"
          fill="none"
          stroke="currentColor"
          strokeWidth="2"
          strokeLinecap="round"
          strokeLinejoin="round"
        >
          <line x1="3" y1="12" x2="21" y2="12"></line>
          <line x1="3" y1="6" x2="21" y2="6"></line>
          <line x1="3" y1="18" x2="21" y2="18"></line>
        </svg>
      </button>

      <div className="mobile-cart-button">
        <CookingPotBasket onClick={handleCartClick} />
      </div>

      {/* Mobile Menu */}
      <div className={`mobile-nav ${isMobileMenuOpen ? "active" : ""}`}>
        <button
          className="close-menu"
          onClick={() => setIsMobileMenuOpen(false)}
          aria-label="Close menu"
        >
          Ã—
        </button>
        <ul className="mobile-menu">
          <li>
            <NavigationLink to="/" onClick={() => setIsMobileMenuOpen(false)}>
              Home
            </NavigationLink>
          </li>
          <li>
            <NavigationLink
              to="/menu"
              onClick={() => setIsMobileMenuOpen(false)}
            >
              Menu
            </NavigationLink>
          </li>
          <li>
            <NavigationLink
              to="/Reservation"
              onClick={() => setIsMobileMenuOpen(false)}
            >
              Book A Table
            </NavigationLink>
          </li>
          <li>
            <NavigationLink
              to="/about"
              onClick={() => setIsMobileMenuOpen(false)}
            >
              About Us
            </NavigationLink>
          </li>
          <li>
            <NavigationLink
              to="/contact"
              onClick={() => setIsMobileMenuOpen(false)}
            >
              Contact
            </NavigationLink>
          </li>
        </ul>
      </div>

      {/* Overlay */}
      {isMobileMenuOpen && (
        <div
          className="overlay active"
          onClick={() => setIsMobileMenuOpen(false)}
        />
      )}
    </nav>
  );
};

export default Navigation;
