import React, { Suspense, lazy, useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  useLocation,
  useNavigate,
} from "react-router-dom";
import { CartProvider } from "./context/CartContext";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import ScrollToTopButton from "./components/ScrollToTopButton";

import "./App.css";

// Load Stripe
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

// Lazy load components
const HomePage = lazy(() => import("./components/HomePage"));
const MenuPage = lazy(() => import("./components/MenuPage"));
const CheckoutPage = lazy(() => import("./components/CheckoutPage"));
const PaymentPage = lazy(() => import("./components/PaymentPage"));
const AboutPage = lazy(() => import("./components/AboutPage"));
const ContactPage = lazy(() => import("./components/ContactPage"));
const Navigation = lazy(() => import("./components/Navigation"));
const FooterPage = lazy(() => import("./components/FooterPage"));
const OrderConfirmationPage = lazy(
  () => import("./components/OrderConfirmationPage")
);
const ReservationPage = lazy(() => import("./components/ReservationPage"));
const ReservationConfirmationPage = lazy(
  () => import("./components/ReservationConfirmationPage")
);

// Enhanced loading spinner
const LoadingSpinner = () => (
  <div className="loading-container">
    <div className="loading-spinner">
      <div className="spinner"></div>
      <p>Loading...</p>
    </div>
  </div>
);

// Page wrapper component to handle mounting/unmounting
const PageWrapper = ({ children }) => {
  const [key, setKey] = useState(0);
  const location = useLocation();

  useEffect(() => {
    // Force remount of components when location changes
    setKey((prev) => prev + 1);

    // Scroll to top
    window.scrollTo(0, 0);

    // Clear any persisted state
    sessionStorage.removeItem("scrollPosition");
  }, [location.pathname]);

  return (
    <div key={key} className="page-wrapper">
      {children}
    </div>
  );
};

function AppContent() {
  const location = useLocation();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);

  const isCheckoutOrPayment = ["/checkout", "/payment"].includes(
    location.pathname
  );
  const isReservationFlow = [
    "/reservation",
    "/reservation-confirmation",
  ].includes(location.pathname);

  useEffect(() => {
    // Handle initial loading
    const handleInitialLoad = async () => {
      setIsLoading(true);
      try {
        // Check if we need to restore any state
        const savedPath = sessionStorage.getItem("currentPath");
        const savedScroll = sessionStorage.getItem("scrollPosition");

        if (savedPath && savedPath !== location.pathname) {
          // Clear saved state
          sessionStorage.removeItem("currentPath");
          sessionStorage.removeItem("scrollPosition");
        }

        // Store current path
        sessionStorage.setItem("currentPath", location.pathname);
      } catch (error) {
        console.error("Error during navigation:", error);
      } finally {
        setIsLoading(false);
      }
    };

    handleInitialLoad();
  }, [location.pathname, navigate]);

  // Handle navigation errors
  useEffect(() => {
    const handleError = (event) => {
      event.preventDefault();
      console.error("Navigation error:", event.error);
      setIsLoading(false);
    };

    window.addEventListener("unhandledrejection", handleError);
    return () => window.removeEventListener("unhandledrejection", handleError);
  }, []);

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="app">
      {!isCheckoutOrPayment && (
        <Suspense fallback={<LoadingSpinner />}>
          <Navigation />
        </Suspense>
      )}

      <main className="main-content">
        <Suspense fallback={<LoadingSpinner />}>
          <Routes>
            <Route
              path="/"
              element={
                <PageWrapper>
                  <HomePage />
                </PageWrapper>
              }
            />
            <Route
              path="/menu"
              element={
                <PageWrapper>
                  <MenuPage />
                </PageWrapper>
              }
            />
            <Route
              path="/checkout"
              element={
                <div className="checkout-wrapper">
                  <PageWrapper>
                    <CheckoutPage />
                  </PageWrapper>
                </div>
              }
            />
            <Route
              path="/payment"
              element={
                <div className="payment-wrapper">
                  <PageWrapper>
                    <PaymentPage />
                  </PageWrapper>
                </div>
              }
            />
            <Route
              path="/reservation"
              element={
                <div className="reservation-wrapper">
                  <PageWrapper>
                    <ReservationPage />
                  </PageWrapper>
                </div>
              }
            />
            <Route
              path="/about"
              element={
                <PageWrapper>
                  <AboutPage />
                </PageWrapper>
              }
            />
            <Route
              path="/contact"
              element={
                <PageWrapper>
                  <ContactPage />
                </PageWrapper>
              }
            />
            <Route
              path="/confirmation"
              element={
                <PageWrapper>
                  <OrderConfirmationPage />
                </PageWrapper>
              }
            />
            <Route
              path="/reservation-confirmation"
              element={
                <div className="reservation-wrapper">
                  <PageWrapper>
                    <ReservationConfirmationPage />
                  </PageWrapper>
                </div>
              }
            />
          </Routes>
        </Suspense>
      </main>

      <ScrollToTopButton />

      {!isCheckoutOrPayment && !isReservationFlow && (
        <Suspense fallback={<LoadingSpinner />}>
          <FooterPage />
        </Suspense>
      )}
    </div>
  );
}

// Error Boundary Component
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    console.error("Error:", error);
    console.error("Error Info:", errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="error-container">
          <h1>Something went wrong.</h1>
          <button onClick={() => window.location.reload()}>Reload Page</button>
        </div>
      );
    }

    return this.props.children;
  }
}

function App() {
  return (
    <Router>
      <ErrorBoundary>
        <CartProvider>
          <Elements stripe={stripePromise}>
            <AppContent />
          </Elements>
        </CartProvider>
      </ErrorBoundary>
    </Router>
  );
}

export default App;
