import React, { useState, useEffect, useRef } from "react";
import "./styles.css";

const ScrollToTopButton = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [isScrollingUp, setIsScrollingUp] = useState(false);
  const lastScrollY = useRef(window.scrollY);

  // Handle scroll event
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      const scrollThreshold = 100;

      // Determine scroll direction and visibility
      setIsVisible(currentScrollY > scrollThreshold);
      setIsScrollingUp(currentScrollY < lastScrollY.current);

      // Update the ref value
      lastScrollY.current = currentScrollY;
    };

    window.addEventListener("scroll", handleScroll, { passive: true });

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Scroll to top function
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  // Determine button classes based on state
  const buttonClasses = `scrollToTopButton ${
    isVisible ? "scroll-show" : "scroll-hide"
  } ${isScrollingUp ? "scrolling-up" : ""}`;

  return (
    <button
      className={buttonClasses}
      onClick={scrollToTop}
      aria-label="Scroll to top"
    />
  );
};

export default ScrollToTopButton;
